var requestBody = "";
var parsedResponse;

var client = new XMLHttpRequest();
client.open("get", "https://dev201115.service-now.com/api/now/table/x_quo_coursehub_course?sysparm_fields=duration%2Ctitle%2Cdescription&sysparm_limit=10");

client.setRequestHeader('Accept', 'application/json');
client.setRequestHeader('Content-Type', 'application/json');

//Eg. UserName="admin", Password="admin" for this code sample.
client.setRequestHeader('Authorization', 'Basic ' + btoa('admin' + ':' + 'mE7QuyPj3=K!'));

client.onreadystatechange = function() {
    if (this.readyState == this.DONE) {
        parsedResponse = JSON.parse(this.response)["result"];
        console.log(parsedResponse[0]);
        displayCourseDetails(parsedResponse[0]);
    }
};
client.send(requestBody);
